import { ConstainerIcon, ContainerInputButton, Image, ImageIcon, MainContainer } from "./style"
import TextField from "@mui/material/TextField"
import Box from "@mui/material/Box"
import { Button, InputAdornment } from "@mui/material"
import SendIcon from '@mui/icons-material/Send';
import { useEffect, useState } from "react";
import api from '../../Services/api'
import iconGit from '../../Img/iconGit.png'
import moment from "moment";







const HomePage = () => {

    const [userGit, setUserGit] = useState({})
    const [searchInput, setSearchInput] = useState('')

    const onChangeSearcheInput = (event) => {
        setSearchInput(event.target.value)
    }

    function getItemLocalStorage() {
        const storage = localStorage.getItem('itens') || []
            const getItemLocalStorage = JSON.parse(storage)
            const updateLocalStorage = [...getItemLocalStorage, searchInput]
            localStorage.setItem('itens', JSON.stringify(updateLocalStorage))
    }


    const getUserGitHub = async () => {

        try {
            const response = await api.get(`${searchInput}`)
            setUserGit(response.data)          


        } catch (error) {

            alert('Erro ao pesquisar usuário !')

        }
    }

    useEffect(() => {
        getUserGitHub()
    }, [])


    const DateFormat = moment(userGit.created_at).format('DD/MM/YYYY')


    return (
        <MainContainer>
            <ConstainerIcon>
                <h1>GitHub Searcher</h1>
                <ImageIcon src={iconGit} />
            </ConstainerIcon>
            <ContainerInputButton>
                <Box
                    sx={{
                        width: 500,
                        maxWidth: '100%'

                    }}
                >
                    <TextField
                        onChange={onChangeSearcheInput}
                        InputProps={{
                            endAdornment: <InputAdornment position="end"> </InputAdornment>,
                            style: { color: "black" }
                        }}
                        sx={{
                            "& .MuiInputLabel-root": { color: 'black', opacity: "90%" },
                            "& .MuiOutlinedInput-root": {
                                "& > fieldset": { borderColor: "black", opacity: "80%", color: "black" }
                            },
                            "& .MuiOutlinedInput-root.Mui-focused": {
                                "& > fieldset": {
                                    borderColor: "black"
                                }
                            },
                            "& .MuiOutlinedInput-root:hover": {
                                "& > fieldset": {
                                    borderColor: "black",
                                    opacity: '90%'
                                }
                            },
                            color: "black"
                        }}

                        fullWidth
                        label="Digite o nome do usuário do GitHub"
                        id="fullWidth" />

                </Box>
                <Button onClick={getUserGitHub} sx={{ color: 'black' }} endIcon={<SendIcon />}>
                    Enviar
                </Button>
            </ContainerInputButton>

            <h3>{userGit.name}</h3>
            <Image src={userGit.avatar_url} />
            <p>{userGit.email}</p>
            <h5>{userGit.bio}</h5>
            <p>Repositórios públicos: {userGit.public_repos}</p>
            <a href={userGit.html_url} target="_blank">Para saber mais, clique aqui</a>
            <p>Usuário desde: {DateFormat}</p>



        </MainContainer>)

}

export default HomePage
